package com.dlex;public class Convert_IntToChar {    public static void main(String[] args) {        int a = 65;        String str1 = Character.toString((char)a);        System.out.println(str1);        int b = 66;        String str2 = Character.toString((char)b);        System.out.println(str2);        char c = (char) 65;        System.out.println(c);        int d = 68;        char toChar = (char) d;        System.out.print(toChar);    }}