package com.dlex;public class Recursion_Print {    public static void main(String[] args) {        printDot(5);    }    public static void printDot(int input) {        if (input == 1) {            System.out.print(".");            System.out.print(input + " ");        } else {            for (int i = input; i > 0; i--) {                System.out.print(".");            }            System.out.print(input + " ");            printDot(input - 1);        }    }}